mapper.select:
	select review_post_id, title, post_html,  view_count, create_date, like_count, user_id
from review_post
where user_id=#{userId};

	select study_id, title, post_html, view_count, post_date, like_count, address, user_id
from study_post
where user_id=#{userId};

	select comments_id, comments_contents, comments_date, review_post_id as reviewId, user_id
from review_post_comments
where user_id=#{userId};

	select comments_id, comments_contents, comments_date, study_id, user_id
from study_post_comments
where user_id=#{userId};
